import React, { useEffect, useState } from "react";

const Bottle = (props) => {
  const [bottle, setBottle] = useState([]);

  const { result, hasScanned, hasResult, setPairBool } = props;
  const id = result.id ? result.id : null;
  

  const getBottle = async () => {
    
    if (id !== null && !hasResult && !hasScanned) {
        setPairBool("hasScanned", true);
      try {
        const response = await fetch(
          `http://localhost:5000/products/${id}`,
          {
            method: "GET",
            headers: { token: localStorage.token },
          }
        );

        const parseData = await response.json();
        console.log(parseData.length);
        if (parseData.length < 0) {
        setBottle(parseData);
        }
        setPairBool("hasResult", true);
        console.log(parseData);
      } catch (err) {
        console.error(err.message);
        setPairBool("hasScanned", false);
      }
    } else {
      console.log("id not defined");
    }
  };

  useEffect(() => {
      if (!hasResult) {
    getBottle();
      }
  }, [id]);

  if (bottle.length < 0) {

  return <h1>{bottle}</h1>;

  } else { 

    return <h1>No bottle found</h1>

  }
};

export default Bottle;
