import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Count from "./Count";

const ShowLists = ({ allLists, setListsChange }) => {
  const [lists, setLists] = useState([]); //assigned to an empty array

  //delete list function
  const deleteList = async (id) => {
    try {
      await fetch(`http://localhost:5000/stocklists/delete/${id}`, {
        method: "DELETE",
        headers: { token: localStorage.token },
      });

      setLists(lists.filter((stocklist) => stocklist.stocklist_id !== id));
    } catch (err) {
      console.error(err.message);
    }

    setListsChange(true);
  };

  useEffect(() => {
    setLists(allLists);
  }, [allLists]);

  console.log(lists);

  //go to count function
  const goToCount = async (id) => {
    console.log(id);
    window.location(`/count/:${id}`)
  };

  return (
    <Fragment>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Stock Area</th>
            <th>Count</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
          {allLists.map((stocklist) => (
            <tr key={stocklist.stocklist_id}>
              <td>{stocklist.stocklist_name}</td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={() => goToCount(stocklist.stocklist_id)}
                >
                  Count
                </button>
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteList(stocklist.stocklist_id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ShowLists;
