import React, { Fragment, useState } from "react";
import Bottle from "../../images/beer-bottle.png";
import { useEffect } from "react";

const CountModal = ({ product, setProductsChange }) => {
  const [quantity, setQuantity] = useState(0);
  const addPartial = async (id) => {
    try {
      const body = { id, quantity };
      const response = await fetch(
        `http://localhost:5000/stocklists/addpartial`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );

      await response.json();

      setProductsChange(true);
    } catch (err) {
      console.error(err.message);
    }
  };

  const addOne = () => {
    setQuantity(quantity + 1);
  };

  const minusOne = () => {
    setQuantity(quantity - 1);
  };

  useEffect(() => {}, [quantity]);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-info"
        data-toggle="modal"
        data-target={`#id${product.product_stocklist_id}`}
      >
        Count
      </button>

      <div className="modal" id={`id${product.product_stocklist_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">{product.product_name}</h4>
            </div>
            <div className="modal-body"></div>
            <div className="container">
              <h5>Add partial</h5>
              <div className="row">
                <div className="col-sm">
                  <form>
                    <input
                      className="form-control mb-4"
                      type="number"
                      placeholder="0.00"
                      step="0.05"
                      min="0"
                      max="1.0"
                    ></input>
                  </form>
                </div>
                <div className="col-sm">
                  <button className="btn btn-info">Add</button>
                </div>
              </div>
              <h5>Add full</h5>
              <div className="row">
                <div className="col-sm">
                  <button className="btn btn-danger" onClick={setQuantity(quantity - 1)}>
                    <h4>-</h4>
                  </button>
                </div>
                <div className="col-sm">
                  <img
                    src={Bottle}
                    alt="Bottle Image"
                    height="140"
                    width="140"
                  />
                </div>
                <div className="col-sm">
                  <button className="btn btn-success" onClick={() => addOne()}>
                    <h4>+</h4>
                  </button>
                </div>
              </div>
              <div className="row">
                <div className="col-sm"></div>
                <div className="col-sm">{quantity}</div>
                <div className="col-sm"></div>
              </div>
            </div>
            <div className="modal-footer">
              <button
                className="btn btn-success"
                data-dismiss="modal"
                onClick={() => addPartial(product.product_stocklist_id)}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default CountModal;
