import React, { Fragment, useState, useEffect } from "react";
import InventoryBreakdown from "./InventoryBreakdown";

const Inventory = (props) => {
  const [inventory, setInventory] = useState([]);
  const [search, setSearch] = useState("")

  //get the latest inventory ID from the params
  const id = props.match.params.id;

  const getInventory = async () => {
    const response = await fetch(`http://localhost:5000/inventory/records/${id}`, {
      method: "GET",
      headers: { token: localStorage.token },
    });
    const parseData = await response.json();
    setInventory(parseData);
  };

  useEffect(() => {
    getInventory();
  }, []);

  return (
    <Fragment>
      <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Inventory</h1>
            <div>{search}<input type="text" placeholder="Search" onChange={ e => setSearch(e.target.value) }/></div>
          </div>
        </div>
        <div>
          <table className="table text-center">
            <thead>
              <tr>
                <th>Product</th>
                <th>Inventory</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {inventory.map((item) => (
                <tr key={item.product_id}>
                  <td>{item.product_name}</td>
                  <td>{item.sum}</td>
                  <td><InventoryBreakdown product_id={item.product_id} id={id} name={item.product_name}/></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </Fragment>
  );
};

export default Inventory;
