import React, { PureComponent } from "react";
import jsPDF from "jspdf";

export default class PdfGenerator extends PureComponent {
  //initialize constructor
  constructor(props) {
    super(props);

    this.state = {};
  }

  // jspdf generator function
jsPdfGenerator = () => {
      //new document in jspdf

      var doc = new jsPDF('p','pt');

      //add some text 
      doc.text(20,20, 'This is default text')

      //set the font of the pdf 
      doc.setFont('courier')

      doc.save("generated.pdf");
  }

  render(){
      return(<button className="btn btn-success" onClick={this.jsPdfGenerator}>Generate PDF Report</button>)
  }
}
