import React, { Fragment, useState, useEffect } from "react";

const Inventory = (props) => {
  const [products, setProducts] = useState([]);

  const [productQuantities, setProductQuantities] = useState([]);

  const getProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/products");
      const jsonData = await response.json();

      setProducts(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  const getTotal = async (product) => {
    let productId = product.product_id;
    let productName = product.product_name;
    let productQuantity = "";
    try {
      const response = await fetch(
        `http://localhost:5000/inventory/${productId}`
      );
      productQuantity = await response.json();
    } catch (err) {
      console.error(err.message);
    }

    var productInventory = { name: productName, quantity: productQuantity };

    productQuantities.push(productInventory);
  };

  console.log(products);

  

  console.log(productQuantities);

  useEffect(() => {
    getProducts();
    products.forEach(getTotal);
    setProductQuantities(productQuantities);
  }, []);

  return (
    <Fragment>
      <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Inventory</h1>
            <div></div>
          </div>
        </div>
        <div>
          <table className="table text-center">
            <thead>
              <tr>
                <th>Product</th>
                <th>Inventory</th>
              </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{productQuantities}</td>
                </tr>
            </tbody>
          </table>
        </div>
      </main>
    </Fragment>
  );
};

export default Inventory;
