import React, { Fragment, useState, useEffect } from "react";

import EditQuantity from "./EditQuantity"

const ListProducts = () => {

    const [products, setProducts] = useState([]);

    //delete product function
    const deleteProduct = async (id) => {
        try {
            const deleteProduct = await fetch(`http://localhost:5000/products/${id}`, {
                method: "DELETE"
            });

            setProducts(products.filter(product => product.product_id !== id));

        } catch (err) {
            console.error(err.message)
        }
    }

    const getProducts = async() => {
        try {

            const response = await fetch("http://localhost:5000/products")
            const jsonData = await response.json()

            setProducts(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    useEffect(() => {
        getProducts();
    }, []);


    return (
        <Fragment>
            <h1 className="text-center mt-5">Product List</h1>
            <table class="table mt-5 text-center">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Quantity</th>
                        <th>Update</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
                    {products.map(products => (
                        <tr key={products.product_id}>
                            <td>{products.product_name}</td>
                            <td>{products.quantity}</td>
                            <td>Update</td>
                            <td><button className="btn btn-danger" onClick={() => deleteProduct(products.product_id)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Fragment>
    );
}

export default ListProducts
