import React, { Fragment, useState } from "react";

const EditTeamMember = ({ member, setTeamChange }) => {
 
 
    const editName = async id => {
    try {
      const body = { name, email };

      const myHeaders = new Headers();

      myHeaders.append("Content-Type", "application/json");
      myHeaders.append("token", localStorage.token);

      await fetch(`http://localhost:5000/teammanagement/update/${id}`, {
        method: "PUT",
        headers: myHeaders,
        body: JSON.stringify(body),
      });

      setTeamChange(true);
    } catch (err) {
      console.log(err.message);
    }
  };

  const [name, setName] = useState(member.user_name);
  const [email, setEmail] = useState(member.user_email);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${member.user_id}`}
      >
        Edit
      </button>
      {}
      <div
        className="modal"
        id={`id${member.user_id}`}
        onClick={() => setName(member.user_name)}
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Edit Details</h4>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                onClick={() => setName(member.user_name)}
              >
                &times;
              </button>
            </div>

            <div className="modal-body">
              <input
                type="text"
                className="form-control"
                value={name}
                onChange={e => setName(e.target.value)}
              />
              <input type="text" 
              className="form-control mt-1" 
              value={email} />
            </div>

            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-warning"
                data-dismiss="modal"
                onClick={() => editName(member.user_id), console.log(email)}
              >
                Edit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                data-dismiss="modal"
                onClick={() => setEmail(member.user_name)}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditTeamMember;
