import React, { Fragment, useState, useEffect } from "react";

import EditQuantity from "./EditQuantity";

const Stocklists = (props) => {
  
  const [products, setProducts] = useState([]);

  //delete product function
  const deleteProduct = async (id) => {
    try {
      const deleteProduct = await fetch(
        `http://localhost:5000/products/${id}`,
        {
          method: "DELETE",
        }
      );

      setProducts(products.filter((product) => product.product_id !== id));
    } catch (err) {
      console.error(err.message);
    }
  };

  const getProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/products");
      const jsonData = await response.json();

      setProducts(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getProducts();
  }, []);



  return (
    <Fragment>
    <main role="main">
        <div className="jumbotron">
        <button className="btn btn-primary float-right" onClick={(e) => logout(e)}>
        Logout
      </button>
          <div className="container">
            <h1 className="display-3">Stock Areas</h1>
            <p>
              Welcome to your BevStock dashboard.
            </p>
          </div>
        </div>
      <h1 className="text-center mt-5">Product List</h1>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
          {products.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_name}</td>
              <td>{product.quantity}</td>
              <td>
                <EditQuantity product={product} />
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteProduct(products.product_id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </main>
    </Fragment>
  );
};

export default Stocklists;
