import React, { Fragment, useState, useEffect, useRef } from "react";
import Dropdown from "./Dropdown";
import Product from "./Product";
import "../../../src/App.css";
import { toast } from "react-toastify";

const ViewEdit = (props) => {
  const [name, setName] = useState([]);
  const [products, setProducts] = useState([]);
  const [productsChange, setProductsChange] = useState(false);
  const [stocktake, setStocktake] = useState(false);
  const [dragging, setDragging] = useState(false);
  const [originalIndex, setOriginalIndex] = useState("");
  const dragItem = useRef();
  const dragNode = useRef();
  const [dragMode, setDragMode] = useState(false);

  const stockId = Number(localStorage.getItem("stocktake"));

  const checkStocktake = () => {
    if (stockId !== null) {
      console.log("stocktake in progress");
      setStocktake(true);
    }
  };

  const id = props.match.params.id;

  const handleDragStart = (e, params) => {
    console.log("drag start", params);
    dragItem.current = params;
    dragNode.current = e.target;
    setOriginalIndex(params);
    dragNode.current.addEventListener("dragend", handleDragEnd);
    setTimeout(() => {
      setDragging(true);
    }, 0);
  };

  const handleDragEnter = (e, params) => {
    const currentItem = dragItem.current;
    if (originalIndex.productI !== params.productI) {
      console.log("Target is not the same");
      setProducts((oldProducts) => {
        let newList = JSON.parse(JSON.stringify(oldProducts));
        newList.splice(
          params.productI,
          0,
          newList.splice(currentItem.productI, 1)[0]
        );
        dragItem.current = params;
        return newList;
      });
      products.map((product, productI) => {
        console.log(product.product_stocklist_id, productI);
      });
    }
  };

  const handleDragEnd = () => {
    console.log("Ending Drag");
    setDragging(false);
    dragNode.current.removeEventListener("dragend", handleDragEnd);
    dragItem.current = null;
    dragNode.current = null;
  };

  async function setPosition(id, productI) {
    try {
      const body = { id, productI };
      const response = await fetch(
        `http://localhost:5000/stocklists/savepositions`,
        {
          method: "POST",
          headers: { "Content-type": "application/json" },
          body: JSON.stringify(body),
        }
      );
      const parseRes = await response.json;
    } catch (error) {}
  }

  async function setPositions() {
    products.map((product, productI) => {
      setPosition(product.product_stocklist_id, productI);
    });
    toast.success("Updated succesfully");
  }

  const getStyles = (params) => {
    const currentItem = dragItem.current;
    if (currentItem.productI === params) {
      return "current dnd-item";
    }
    return "dnd-item";
  };

  //get list name
  const getName = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/get/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      setName(parseRes.rows[0].stocklist_name);
    } catch (err) {
      console.error(err.message);
    }
  };

  //get products on list
  const getProducts = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/list/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      setProducts(parseRes);
    } catch (err) {
      console.error(err.message);
    }
  };

  //delete a product from list
  const deleteProduct = async (id) => {
    try {
      await fetch(`http://localhost:5000/stocklists/deleteproduct/${id}`, {
        method: "DELETE",
        headers: { token: localStorage.token },
      });
    } catch (err) {
      console.error(err.message);
    }

    setProductsChange(true);
  };

  useEffect(() => {
    checkStocktake();
    getName(id);
    getProducts(id);
    setProductsChange(false);
  }, [productsChange]);

  return (
    <Fragment>
      <main role="main">
        <div className="container">
          <h1 className="display-3">{name}</h1>
          <div>
            <Dropdown listId={id} setProductsChange={setProductsChange} />
          </div>
          <div className="saveButton">
            <button onClick={setPositions}>Save List Positions</button>
          </div>
        </div>
        <table className="table mt-5 text-center">
          <thead>
            <tr>
              <th>Product</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product, productI) => (
              <tr
                draggable
                onDragStart={(e) => {
                  handleDragStart(e, { productI });
                }}
                onDragEnter={
                  dragging
                    ? (e) => {
                        handleDragEnter(e, { productI });
                      }
                    : null
                }
                key={product.product_stocklist_id}
                className={dragging ? getStyles(productI) : "dnd-item"}
              >
                <Product
                  key={product.product_stocklist_id}
                  name={product.product_name}
                  id={product.product_stocklist_id}
                />
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </Fragment>
  );
};

export default ViewEdit;
