import React, { Fragment, useState } from "react";
import { toast } from "react-toastify";

const AddPartial = ({ product, setProductsChange }) => {
  const addPartial = async (id) => {
    try {
      console.log("id", id);
      await fetch(`http://localhost:5000/stocklists/addpartial/${id}`, {
        method: "PUT",
        body: 0.25,
      });

      setProductsChange(true);

    } catch (err) {
      console.error(err.message);
    }
  };

  const [quantity, setQuantity] = useState([]);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-info"
        data-toggle="modal"
        data-target={`#id${product.product_stocklist_id}`}
      >
        Add Partial
      </button>

      <div className="modal" id={`id${product.product_stocklist_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Add Partial Bottle</h4>
            </div>
            <div className="modal-body"></div>
            <form className="form-contol">
              <input
                className="form-control mb-4"
                type="number"
                placeholder="0.00"
                step="0.05"
                min="0"
                max="1.0"
                onChange={(e) => setQuantity(e.target.value)}
              ></input>
            </form>
            <div className="modal-footer">
              <button
                className="btn btn-success"
                onClick={() => addPartial(product.product_stocklist_id)}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AddPartial;
