import React, { useEffect, useState } from "react";

const ProductAlerts = ({ levels }) => {
  return (
    <div>
    <h5>Low Product Alerts</h5>
      <table className="table text-center">
        <thead>
          <tr>
            <th>Product</th>
            <th>Recomended</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {levels.map((item) => {
            if (item.par_level > Number(item.sum))
              return (
                <tr key={item.product_id}>
                  <td>{item.product_name}</td>
                  <td>{item.par_level}</td>
                  <td>{item.sum}</td>
                </tr>
              );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ProductAlerts;
