import React, { useEffect, useState } from "react";

const ProductAlerts = ({ levels }) => {

    return(
        <table className="table text-center">
            <thead>
                <tr>
                    <th>Product</th>
                    <th>Reccomended</th>
                    <th>Quantity</th>
                </tr>
            </thead>
            <tbody>
                {levels.map((item) => (
                    <tr key={item.product_id}>
                        <td>{item.product_name}</td>
                        <td>{item.par_level}</td>
                        <td>{item.sum}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    )
}

export default ProductAlerts;
