import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";

import { toast } from "react-toastify";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.user_name);
    } catch (err) {
      console.log(err.message);
    }
  }

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logged out succsessfully!");
  };

  useEffect(() => {
    getName();
  }, []);

  return (
    <Fragment>
      <main role="main">
        <div class="jumbotron">
        <button className="btn btn-primary" onClick={(e) => logout(e)}>
        Logout
      </button>
          <div class="container">
            <h1 class="display-3">Hello, {name}!</h1>
            <p>
              Welcome to your BevStock dashboard.
            </p>
          </div>
        </div>

        <div class="container">
          <div class="row">
            <div class="col-md-4">
              <h2>Stocktaking</h2>
              <p>
                <a class="btn btn-secondary" href="#" role="button">
                  Stocktaking home &raquo;
                </a>
              </p>
            </div>
            <div class="col-md-4">
              <h2>Team Management</h2>
              <p>
                <a class="btn btn-secondary" href="#" role="button">
                  Manage my team &raquo;
                </a>
              </p>
            </div>
            <div class="col-md-4">
              <h2>Inventory</h2>
              <p>
                <a class="btn btn-secondary" href="#" role="button">
                  View Inventory &raquo;
                </a>
              </p>
            </div>
          </div>
        </div>
      </main>

      <footer class="container">
        <p>&copy; Company 2017-2018</p>
      </footer>

     
      <Link to="/listproducts">Stocktaking</Link>
    </Fragment>
  );
};

export default Dashboard;
