import React, { Fragment, useState, useEffect } from "react";

const InventoryBreakdown = ({ product_id, props }) => {
  const product = product_id;
  const id = props.match.params.id;

  const getInventoryBreakdown = async () => {
    try {
      const body = { id, product };
      const response = await fetch(
        "http://localhost:5000/inventory/breakdown",
        {
          method: "POST",
          headers: { "Content-type": "application/json" },
          body: JSON.stringify(body),
        }
      );
      const parseRes = await response.json();
      console.log(parseRes);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    getInventoryBreakdown();
  }, []);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-info"
        data-toggle="modal"
        data-target={`#id${product_id}`}
      >
        View Breakdown
      </button>
      <div className="modal" id={`id${product_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">{product_id}</h4>
            </div>
            <div className="modal-body"></div>

            <div className="modal-footer">
              <button className="btn btn-success" data-dismiss="modal">
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default InventoryBreakdown;
