import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";

const TopSellerChart = ({ products, currentStocktake, prevStocktake }) => {
  const [topSellers, setTopSellers] = useState([]);
  const [sales, setSales] = useState([]);
  const [chartData, setChartData] = useState({});
  const [labels, setLabels] = useState([]);

  const getSales = async () => {
    var promises = products.map(function (product) {
      return getSalesData(product.product_id).then(function (results) {
        product.rows = results;
        return product;
      });
    });

    Promise.all(promises).then(function (results) {
        console.log(results[0].rows.product_name);
      setSales(results);
    setLabels(results.map((result) => {
        return result.rows.product_name;
    }))
    });
  };

  const getSalesData = async (product) => {
    try {
      const body = { prevStocktake, currentStocktake, product };
      const response = await fetch(`http://localhost:5000/inventory/sales`, {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      const parseRes = await response.json();
      return parseRes;
    } catch (error) {
      console.error(error.message);
    }
  };

  const getData = () => {
    setChartData({
      labels: labels,
      datasets: [{
          label: 'Sales',
          data: [
              0, 5
          ],
      }]
    });
  };

  useEffect(() => {
    if (products.length > 0) {
      getSales();
      getData();
    }
    if (sales.length > 0) {
        console.log("Sales Populated")
      getData();
    }
  }, [products]);

  return (
    <div>
      <h1>Top Seller Chart</h1>
      <h1>{currentStocktake}</h1>
      <h1>{prevStocktake}</h1>
      {console.log(sales)}
      {console.log(labels)}
      <Bar width={100} height={50} data={{
          labels: labels,
          datasets: [{
              label: 'Sales',
              data: [0, 5
              ],
          }]
      }} />
    </div>
  );
};

export default TopSellerChart;
