import React, { Fragment, useState, useEffect } from "react";
import ListProducts from "./ListProducts";

const Count = ({}) => {
  const [stocklistName, setStocklistName] = useState("");
  const [stocklistProducts, setStocklistProducts] = useState("");

  const queryString = window.location.search;
  console.log(queryString);

  const split = queryString.split("=");
  const id = split[1];
  console.log(id);

  async function getStocklistName() {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/get/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      setStocklistName(parseRes.rows[0].stocklist_name);
    } catch (err) {
      console.error(err.message);
    }
  }

  async function getStocklistProducts() {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/list/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      setStocklistProducts(parseRes);

      console.log(stocklistProducts);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getStocklistName();
    getStocklistProducts();
  }, []);

  getStocklistProducts();

  return (
    <Fragment>
      <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">{stocklistName}</h1>
            <div>
              <ListProducts />
            </div>
          </div>
        </div>
        <div></div>
      </main>
    </Fragment>
  );
};
export default Count;
