import React, { Fragment, useState } from "react";

const AddList = () => {
  const [stocklistName, setStocklistName] = useState("");

  const onSubmitForm = async e => {
      e.preventDefault();
      try {

        const body = { stocklistName };

        const response = await fetch("http://localhost:5000/stocklists", {
        method: "POST",
        headers: {"Content-type": "application/json"},
        body
    })

    console.log(response);
          
      } catch (err) {
          console.error(err.message);
      }
  };

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-primary"
        data-toggle="modal"
        data-target="#myModal"
      >
        New List
      </button>

      <div class="modal" id="myModal">
      <form onSubmit={onSubmitForm}>
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title">New List</h4>
              <button type="button" class="close" data-dismiss="modal">
                &times;
              </button>
            </div>

            <div class="modal-body">
              <a>List Name</a>
                <input
                  type="text"
                  className="form-control"
                  value={stocklistName}
                  onChange={(e) => setStocklistName(e.target.value)}
                />
              </div>

              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-danger"
                  data-dismiss="modal"
                >
                  Close
                </button>
                <button
                  type="button"
                  class="btn btn-success"
                >
                  Add
                </button>
              </div>
            </div>
          </div>
          </form>
        </div>
    
    </Fragment>
  );
};

export default AddList;
