              Edit
            </button>
            <button
              type="button"
              className="btn btn-danger"
              data-dismiss="modal"
              onClick={() => setDescription(todo.description)}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditTeamMember;
