import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";

const ShowLists = ({ allLists, setListsChange }) => {
  const [lists, setLists] = useState([]); //assigned to an empty array

  //delete list function
  const deleteList = async (id) => {
    try {
      await fetch(`http://localhost:5000/stocklists/delete/${id}`, {
        method: "DELETE",
        headers: { token: localStorage.token },
      });

      setLists(lists.filter((stocklist) => stocklist.stocklist_id !== id));
    } catch (err) {
      console.error(err.message);
    }

    setListsChange(true);
  };

  const stocktake = () => {
    window.confirm("Start stocktake for today") ? console.log("Confirmed") : console.log("you clicked no!");
  }

  useEffect(() => {
    setLists(allLists);
  }, [allLists]);

  return (
    <Fragment>
      <table className="table text-center">
        <thead>
          <tr>
            <th>Stock Area</th>
            <th>Count</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {allLists.map((stocklist) => (
            <tr key={stocklist.stocklist_id}>
              <td>{stocklist.stocklist_name}</td>
              <td>
                <button className="btn btn-success"
                onClick={() => stocktake()}>
                  Stocktake
                </button>
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteList(stocklist.stocklist_id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ShowLists;
