import React, { Fragment, useState } from "react";
import Bottle from "../../images/beer-bottle.png";

const AddPartial = ({ product, setProductsChange }) => {
  const addPartial = async (id) => {
    try {
      const body = { id, quantity };
      const response = await fetch(
        `http://localhost:5000/stocklists/addpartial`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );

      await response.json();

      setProductsChange(true);
    } catch (err) {
      console.error(err.message);
    }
  };

  const [quantity, setQuantity] = useState([]);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-info"
        data-toggle="modal"
        data-target={`#id${product.product_stocklist_id}`}
      >
        Count
      </button>

      <div className="modal" id={`id${product.product_stocklist_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">{product.product_name}</h4>
            </div>
            <div className="modal-body"></div>
            <div className="container">
              <div className="row">
                <div className="col-sm">
                  <button className="btn btn-danger">
                    <h4>-</h4>
                  </button>
                </div>
                <div className="col-sm">
                  <img
                    src={Bottle}
                    alt="Bottle Image"
                    height="140"
                    width="140"
                  />
                </div>
                <div className="col-sm">
                <button className="btn btn-success">
                    <h4>+</h4>
                  </button>
                </div>
              </div>
              <div className="row">
                1
              </div>
            </div>
            <div className="modal-footer">
              <button
                className="btn btn-success"
                data-dismiss="modal"
                onClick={() => addPartial(product.product_stocklist_id)}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AddPartial;
