import React, { useState, useEffect } from "react";
import MultiSelect from "react-multi-select-component";
import { toast } from "react-toastify";

const Dropdown = ({ listId, setProductsChange }) => {

  const [products, setProducts] = useState([]);
  const [stocktakeBoo, setStocktakeBoo] = useState(false);

  const stocktake = localStorage.getItem("stocktake");

  const checkStocktake = () => {
    if (stocktake !== null) {
      console.log("stocktake in progress");
      setStocktakeBoo(true);
    }
  };

  const getProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/products");
      const jsonData = await response.json();

      setProducts(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    checkStocktake();
    getProducts();
  }, []);

  const options = products.map((product) => ({
    label: product.product_name,
    value: product.product_id,
  }));
  const [selected, setSelected] = useState([]);


  const addItem = async (item) => {
    let productId = item.value;
    try {
      const body = { productId, listId }
      const response = await fetch("http://localhost:5000/stocklists/addproduct", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      const JsonData = await response.json();
      const id = JsonData.rows[0].product_stocklist_id;

      if (stocktakeBoo) {
        const body = { id, stocktake }
        const stockresponse = await fetch("http://localhost:5000/stocktake/generate", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        });
        const result = await stockresponse.json();
        console.log(result);
      }


      toast.success("Products Added Successfully!");
      setProductsChange(true);
    } catch (err) {
      console.log(err.message)
    }
  }

  const onSubmitForm = async (e) => {
    e.preventDefault();
    selected.forEach(addItem);
  }

  return (
    <div className="container">
    <form onSubmit={onSubmitForm}>
      <div className="row">
        <div className="col-8">
          
          <MultiSelect
            options={options}
            value={selected}
            onChange={setSelected}
            labelledBy={"Select"}
          />
          </div>
          <div className="col-4">
            <button className="btn btn-success">Add</button>
            </div>
        
      </div>
      </form>
    </div>
  );
};

export default Dropdown;
