import React, { Fragment, useState, useEffect } from "react";

//components
import InventoryList from "./InventoryList";

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const productQuantities = useState([]);

  const getProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/products", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const jsonData = await response.json();
      setProducts(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  const getTotal = async (id) => {
    const response = await fetch(`http://localhost:5000/inventory/${id}`, {
      method: "GET",
      headers: { token: localStorage.token },
    });

    console.log(response.json())

  };

  const productIdArray = products.map((product) => product.product_id);

  console.log(productIdArray);

  useEffect(() => {
    getProducts();
  }, []);

  productIdArray.forEach(getTotal);

  return (
    <Fragment>
      <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Inventory</h1>
            <div></div>
          </div>
        </div>
        <div></div>
      </main>
    </Fragment>
  );
};

export default Inventory;
