import React from "react";
import { useEffect, useState } from "react";
import _ from "lodash";

const Test = () => {
  const [products, setProducts] = useState([]);
  const [productsLoaded, setProductsLoaded] = useState(false);
  const [salesLoaded, setSalesLoaded] = useState(false);
  const prevStocktake = localStorage.getItem("secondlaststocktake");
  const currentStocktake = localStorage.getItem("laststocktake");
  const [sales, setSales] = useState([]);

  const [data, setData] = useState([]);
  const [labels, setLabels] = useState([]);

  async function getProducts() {
    try {
      const response = await fetch(
        "http://localhost:5000/products/get/distinct",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );
      const parseRes = await response.json();
      setProducts(parseRes)
      setProductsLoaded(true);
    } catch (error) {
      console.error(error.message);
    }
  }

  const getSalesData = async (product) => {
    try {
      const body = { prevStocktake, currentStocktake, product };
      const response = await fetch(`http://localhost:5000/inventory/sales`, {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      const parseRes = await response.json();
      sales.push(parseRes);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    if (!productsLoaded) {
      getProducts();
    } 
    else if (productsLoaded && !salesLoaded) {
        products.forEach((product) => {
            getSalesData(product.product_id);
        })
      
      setSalesLoaded(true);
    } else if (salesLoaded) {

    }
  });


  return _.isEmpty(products) ? (
    <div className="container-fluid">
      <div className="row">
        <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
          <h1>Loading</h1>
        </main>
      </div>
    </div>
  ) : (
    <div className="container-fluid">
      <div className="row">
        <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
          <h1>{console.log(sales)}Products loaded</h1>
        </main>
      </div>
    </div>
  );
};

export default Test;
