import React, { useState, useEffect } from "react";
import ShowTeam from "./ShowTeam";

//components

const TeamManagement = () => {
    const [teamMembers, setTeamMembers] = useState([]);
    const [teamChange, setTeamChange] = useState(false);

    const getTeam = async () => {
        try {
            const response = await fetch("http://localhost:5000/teammanagement/", {
                method: "GET",
                headers: { token: localStorage.token },
            });

            const parseData = await response.json();

            setTeamMembers(parseData);


        } catch (err) {
            console.error(err.message);
        }
    }

    return (
        <main role="main">
          <div className="jumbotron">
            <div className="container">
              <h1 className="display-3">Stock Areas</h1>
            </div>
          </div>
          <div>
            <ShowTeam teamMembers={teamMembers} setTeamChange={setTeamChange} />
          </div>
        </main>
      );
    };
    
    export default TeamManagement;
