import React from "react";
import _ from "lodash";
import { useEffect, useState } from "react";

const ProductAlerts = ({ stocktake }) => {
  const [levels, setLevels] = useState([]);

  async function getLevels(stocktake) {
    console.log(stocktake);
    try {
      const response = await fetch(
        `http://localhost:5000/inventory/levels/${stocktake}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );
      const parseRes = await response.json();
      setLevels(parseRes);
    } catch (err) {
      console.log(err.message);
    }
  }

  useEffect(() => {
    getLevels();
  }, []);

  return (
    <div>
      {console.log(levels)}
      <h6 className="mb-3">Low Product Alerts{stocktake}</h6>
      <table className="table table-striped table-sm">
        <thead>
          <tr>
            <th>Product</th>
            <th>Recomended</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {levels.map((item) => {
            if (item.par_level > Number(item.sum)) {
              return (
                <tr key={item.product_id}>
                  <td>{item.product_name}</td>
                  <td>{item.par_level}</td>
                  {Number(item.sum) === 0 ? (
                    <td>
                      <span className="badge badge-pill badge-danger">
                        Out Of Stock
                      </span>
                    </td>
                  ) : (
                    <td>
                      <span className="badge badge-pill badge-warning">
                        Running Low: {item.sum}
                      </span>
                    </td>
                  )}
                </tr>
              );
            }
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ProductAlerts;
