import React from "react";
import _ from "lodash";
import PredictedOrderModal from "./PredictedOrderModal";

const ProductAlerts = ({ levels }) => {
  return (
    <div>
    <PredictedOrderModal/>
      {levels.map((item) => {
        if (item.sum - item.avg_weekly_sales < 0) {
          console.log(
            item.product_name,
            Math.abs(Number(item.sum - item.avg_weekly_sales)) +
              item.avg_weekly_sales
          );
        } else {
          console.log(item.product_name, item.avg_weekly_sales, item.sum);
        }
      })}
      <h6 className="mb-3">Low Product Alerts</h6>
      <table className="table table-striped table-sm">
        <thead>
          <tr>
            <th>Product</th>
            <th>Avg Weekly Sales</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {levels.map((item) => {
            if (item.avg_weekly_sales > Number(item.sum)) {
              return (
                <tr key={item.product_id}>
                  <td>{item.product_name}</td>
                  <td>{item.avg_weekly_sales}</td>
                  {Number(item.sum) === 0 ? (
                    <td>
                      <span className="badge badge-pill badge-danger">
                        Out Of Stock
                      </span>
                    </td>
                  ) : (
                    <td>
                      <span className="badge badge-pill badge-warning">
                        Running Low: {item.sum}
                      </span>
                    </td>
                  )}
                </tr>
              );
            }
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ProductAlerts;
