import React, { Fragment, useState, useEffect } from "react";
import { Link, Redirect } from "react-router-dom";
import { FaBeer } from "react-icons/fa";
import { AiOutlineHome } from "react-icons/ai";
import { MdLocalBar } from "react-icons/md";
import { RiScales2Fill } from "react-icons/ri";
import { BsCardChecklist } from "react-icons/bs";
import TopSellerChart from "../components/TopSellerChart";
import ProductAlerts from "../components/inventory/ProductAlerts";

import { toast } from "react-toastify";
import DashBoardStocktaking from "./DashboardStocktaking";
import StockRecords from "./inventory/StockRecords";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");
  const [stocktake, setStocktake] = useState(false);
  const [latestInventoryDate, setLatestInventoryDate] = useState("");
  const [latestInventoryId, setLatestInventoryId] = useState("");
  const [previousInventory, setPreviousInventory] = useState("");
  const [products, setProducts] = useState([]);
  const [levels, setLevels] = useState([]);

  async function getSalesData() {
    try {
      const response = await fetch(
        "http://localhost:5000/products/get/distinct",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );
      const parseRes = await response.json();
      console.log(parseRes);
      setProducts(parseRes);
    } catch (error) {
      console.error(error.message);
    }
  }

  async function checkStocktake() {
    try {
      const response = await fetch(
        "http://localhost:5000/stocktake/activestocktake",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      if (parseRes.length > 0) {
        setStocktake(parseRes);
        localStorage.setItem("stocktake", parseRes[0].stocktake_id);
        localStorage.setItem("stocktakedate", parseRes[0].stocktake_date);
      } else {
        console.log("no stocktake in progress");
      }
    } catch (error) {
      console.error(error.message);
    }
  }

  const stockId = Number(localStorage.getItem("stocktake"));

  const userType = localStorage.getItem("role");

  const Stocktake = () => {
    if (stockId !== 0) {
      console.log("stocktake in progress");
      setStocktake(true);
    }
  };

  async function findLastStocktake() {
    try {
      const response = await fetch("http://localhost:5000/inventory/latest", {
        method: "GET",
        headers: { token: localStorage.token },
      });
      const parseRes = await response.json();
      setLatestInventoryDate(parseRes[0].stocktake_date);
      setLatestInventoryId(parseRes[0].stocktake_id);
      setPreviousInventory(parseRes[1].stocktake_id);

      console.log(latestInventoryDate);
    } catch (error) {
      console.error(error.message);
    }
  }

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.user_name);
      console.log(userType);
    } catch (err) {
      console.log(err.message);
    }
  }

  async function getLevels() {
    try {
      const response = await fetch(
        `http://localhost:5000/inventory/levels/${latestInventoryId}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      console.log(parseRes);
      setLevels(parseRes);
    } catch (err) {
      console.log(err.message);
    }
  }

  const logout = (e) => {
    e.preventDefault();
    localStorage.clear();
    setAuth(false);
    toast.success("Logged out succsessfully!");
  };

  useEffect(() => {
    findLastStocktake();
    checkStocktake();
    Stocktake();
    getName();
    getSalesData();
    if (latestInventoryId !== "") {
      getLevels();
    }
  }, [latestInventoryId]);

  return userType == "User" ? (
    <Redirect to="/userdashboard" />
  ) : (
    <Fragment>
      <div className="container-fluid">
        <div className="row">
         

          <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pb-2 mb-3 border-bottom">
              <h1 className="h2">Welcome back, {name}!</h1>
              <div className="btn-toolbar mb-2 mb-md-0"></div>
            </div>

            <div className="row">
              <div className="col-md-6">
                <TopSellerChart
                  products={products}
                  currentStocktake={latestInventoryId}
                  prevStocktake={previousInventory}
                />
              </div>
              <div className="col-md-6">
                <ProductAlerts levels={levels} />
              </div>
            </div>
            <div className="row">
              <div className="col-md-6">
                <StockRecords />
              </div>
              <div className="col-md-6">
                <DashBoardStocktaking stocktake={stocktake} />
              </div>
            </div>
          </main>
        </div>
      </div>
    </Fragment>
  );
};

export default Dashboard;
