import React, { Fragment, useState, useEffect } from "react";
import PDFPredictedOrder from "./PDFPredictedOrder";
import _ from "lodash";

const PredictedOrderModal = ({ levels }) => {
  const filteredItems = levels.filter(function (el) {
    return el.sum - el.avg_weekly_sales < 0;
  });

  console.log(filteredItems);

  const predictedOrder = levels.filter((item) => {
    if (item.sum - item.avg_weekly_sales < 0) {
      return {
        name: item.product_name,
        size: item.product_size,
        category: item.product_category,
        quantity:
          Math.abs(Number(item.sum - item.avg_weekly_sales)) +
          item.avg_weekly_sales,
      };
    } else {
      return null;
    }
  });

  const arrayFilter = (level) => {
    return level.sum - level.avg_weekly_sales > 0;
  };

  return (
    <Fragment>
      {console.log(predictedOrder)}
      <button
        type="button"
        className="btn btn-primary btn-sm float-right mb-2"
        data-toggle="modal"
        data-target="#predictedOrder"
      >
        View Predicted Order
      </button>
      {console.log(levels)}
      <div className="modal" id="predictedOrder">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Predicted Order</h4>
            </div>
            <div className="modal-body"></div>
            <table className="table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Size</th>
                  <th>Category</th>
                  <th>Quantity</th>
                </tr>
              </thead>
              <tbody>
                {levels.map((item) => {
                  if (item.sum - item.avg_weekly_sales < 0) {
                    return (
                      <tr key={item.product_id}>
                        <td>{item.product_name}</td>
                        <td>{item.product_size}</td>
                        <td>{item.product_category}</td>

                        <td>
                          {Math.abs(Number(item.sum - item.avg_weekly_sales)) +
                            item.avg_weekly_sales}
                        </td>
                      </tr>
                    );
                  } else {
                    console.log(
                      item.product_name,
                      item.avg_weekly_sales,
                      item.sum
                    );
                  }
                })}
              </tbody>
            </table>
            <div className="modal-footer">
              {_.isEmpty(levels) ? null : <PDFPredictedOrder levels={levels} />}
              <button className="btn btn-success" data-dismiss="modal">
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default PredictedOrderModal;
