import React from "react";
import { useEffect } from "react";
import { useState } from "react";

const Test2 = () => {
  const [sales, setSales] = useState([]);
  const prevStocktake = localStorage.getItem("secondlaststocktake");
  const currentStocktake = localStorage.getItem("laststocktake");

  const getSalesData = async (product) => {
    try {
      const body = { prevStocktake, currentStocktake, product };
      const response = await fetch(`http://localhost:5000/inventory/sales`, {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      return response
    } catch (error) {
      console.error(error.message);
    }
  };

  const getProducts = () => {
    try {
      fetch("http://localhost:5000/products/get/distinct", {
        method: "GET",
        headers: { token: localStorage.token },
      })
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          return data.forEach((product) => {
            getSalesData(product);
          });
        }).then((response => {
            return response.json();
        })).then((data) => {
            console.log(data);
        });
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
      TEST2
      {console.log(sales)}
    </main>
  );
};

export default Test2;
