import React, { Fragment, useState, useEffect } from "react";

//components
import AddList from "./AddList";

const Stocklists = () => {
  
  const [allLists, setLists] = useState([]);

  
  const getLists = async () => {
    try {
      const response = await fetch("http://localhost:5000/stocklists", {
        method: "GET",
        headers: { jwt_token: localStorage.token }
        
      });
      
      const jsonData = await response.json();

      console.log(jsonData);

      setLists(jsonData);

    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getLists();
  }, []);



  return (
    <Fragment>
    <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Stock Areas</h1>
            <p><AddList /></p>
          </div>
        </div>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Stock Area</th>
            <th>Quantity</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
          {allLists.map((stocklist) => (
            <tr key={stocklist.stocklist_id}>
              <td>{stocklist.stocklist_name}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </main>
    </Fragment>
  );
};

export default Stocklists;
