import React, { Fragment, useState, useEffect } from "react";

//components
import AddList from "./AddList";

const Stocklists = () => {
  
  const [allLists, setLists] = useState([]);


  //get all lists
  const getLists = async () => {
    try {
      const response = await fetch("http://localhost:5000/stocklists", {
        method: "GET",
        headers: { jwt_token: localStorage.token }

      });
      
      const jsonData = await response.json();

      setLists(jsonData);

    } catch (err) {
      console.error(err.message);
    }
  };

  //delete a list 
  const deleteList = async (id) => {
    try {
      await fetch(`http://localhost:5000/stocklists/delete/${id}`, {
        method: "DELETE",
        headers: { jwt_token: localStorage.token }
      }
      );

      setLists(allLists.filter((stocklist) => stocklist.stocklist_id));
    } catch (err) {
      console.error(err.message);
    }

    window.location = "/stocktaking";

  }

  useEffect(() => {
    getLists();
  }, []);



  return (
    <Fragment>
    <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Stock Areas</h1>
            <p><AddList /></p>
          </div>
        </div>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Stock Area</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
          {allLists.map((stocklist) => (
            <tr key={stocklist.stocklist_id}>
              <td>{stocklist.stocklist_name}</td>
              <td>
                <button
                className="btn btn-danger"
                onClick={() => deleteList(stocklist.stocklist_id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </main>
    </Fragment>
  );
};

export default Stocklists;
