import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import _ from "lodash";
import pool from "../../../server/db";

const UserDashboard = ({ setAuth }) => {
  const [name, setName] = useState("");
  const [stocktake, setStocktake] = useState({});
  const [assignedLists, setAssignedLists] = useState([]);

  async function checkStocktake() {
    try {
      const response = await fetch(
        "http://localhost:5000/stocktake/activestocktake",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      if (parseRes.length > 0) {
        setStocktake(parseRes);
        localStorage.setItem("stocktake", parseRes[0].stocktake_id);
        localStorage.setItem("stocktakedate", parseRes[0].stocktake_date);
        checkAssignedDuties();
      } else {
        console.log("no stocktake in progress");
      }
    } catch (error) {
      console.error(error.message);
    }
  }

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      console.log(parseRes);
      localStorage.setItem("user", parseRes.user_id);
      setName(parseRes.user_name);
    } catch (err) {
      console.log(err.message);
    }
  }

  const logout = (e) => {
    e.preventDefault();
    localStorage.clear();
    setAuth(false);
  };

  //check if user has been assigned any stocktake duties
  async function checkAssignedDuties() {
    try {
      const stocktake_id = localStorage.getItem("stocktake");
      const user_id = localStorage.getItem("user");
      const body = { user_id, stocktake_id };
      const response = await fetch(
        "http://localhost:5000/stocklists/userassignedlists",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );
      const parseRes = await response.json();
    } catch (error) {
      console.error(error.message);
    }
  }

  useEffect(() => {
    getName();
    checkStocktake();
  }, []);

  return (
    <Fragment>
      <main role="main">
        <div className="jumbotron">
          <button
            className="btn btn-primary float-right"
            onClick={(e) => logout(e)}
          >
            Logout
          </button>
          <div className="container">
            <h1 className="display-3">Hello, {name}!</h1>
            <p>Welcome to your BevStock user dashboard.</p>
          </div>
        </div>

        <div className="container">
          <div className="row">
            <div className="col-md-4">
              <h2>Stocktaking</h2>

              {!_.isEmpty(stocktake) ? (
                <div>
                  <p>
                    Stocktake in progress :{" "}
                    {localStorage.getItem("stocktakedate")}
                  </p>
                  <Link className="btn btn-secondary" to="/userstocktaking">
                    Perform a count
                  </Link>
                </div>
              ) : (
                <p>No stocktake in progress</p>
              )}
            </div>
            <div className="col-md-4">
              <h2>Inventory</h2>
              <p>
                <Link className="btn btn-secondary" to="/inventory">
                  Inventory
                </Link>
              </p>
            </div>
          </div>
        </div>
      </main>
    </Fragment>
  );
};

export default UserDashboard;
