import React, { Fragment, useState, useEffect } from "react";
import Dropdown from "./Dropdown";
import CountModal from "./AddPartial";




const Count = (props) => {
  const [name, setName] = useState([]);
  const [products, setProducts] = useState([]);
  const [productsChange, setProductsChange] = useState(false);
  const [quantity, setQuantity] = useState(0);

  // get the list id from the url params
  const id = props.match.params.id;

  //get list name using list id
  const getName = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/get/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      // API call returns an array of length 1,so get 1st value
      setName(parseRes.rows[0].stocklist_name);
    } catch (err) {
      console.error(err.message);
    }
  };

  //get products on list
  const getProducts = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:5000/stocklists/list/${id}`,
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      setProducts(parseRes.map(product => ({ id: product.product_stocklist_id, name: product.product_name, quantity: quantity})));
      console.log(parseRes);
    } catch (err) {
      console.error(err.message);
    }
  };

  //delete a product from list
  const deleteProduct = async (id) => {
    try {
      await fetch(`http://localhost:5000/stocklists/deleteproduct/${id}`, {
        method: "DELETE",
        headers: { token: localStorage.token },
      });
    } catch (err) {
      console.error(err.message);
    }

    setProductsChange(true);
  };

  useEffect(() => {
    getName(id);
    getProducts(id);
    console.log(products[0].quantity)
  }, [productsChange]);

  return (
    <Fragment>
      <main role="main">
        <div className="container">
          <h1 className="display-3">{name}</h1>
          <div>
            <Dropdown listId={id} setProductsChange={setProductsChange} />
          </div>
        </div>
        <table className="table mt-5 text-center">
          <thead>
            <tr>
              <th>Product</th>
              <th>Delete</th>
              <th>Quantity</th>
              <th>Count</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>

                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteProduct(product.id)}
                  >
                    Delete
                  </button>
                </td>
                <td>
              {product.quantity}
                </td>
                <td>
                  <CountModal product={product} setProductsChange={setProductsChange}/>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </Fragment>
  );
};
export default Count;
