import React, { Fragment, useState } from "react";
import Bottle from "../../images/beer-bottle.png";
import { useEffect } from "react";

const CountModal = ({ product, setProductsChange }) => {
  const [quantity, setQuantity] = useState(0);
  const [partial, setPartial] = useState(0);

  const onChange = (e) => {
    setPartial(Number(e.target.value));
  };

  const addPartial = () => {
    setQuantity(Number(quantity + partial));
    setPartial(0);
  }

  const addOne = () => {
    setQuantity(Number(quantity + 1));
  };

  const minusOne = () => {
    setQuantity(Number(quantity - 1));
  };

  useEffect(() => {}, [quantity]);

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-info"
        data-toggle="modal"
        data-target={`#id${product.product_stocklist_id}`}
      >
        Count
      </button>

      <div className="modal" id={`id${product.product_stocklist_id}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">{product.product_name}</h4>
            </div>
            <div className="modal-body"></div>
            <div className="container">
              <h5>Add partial</h5>
              <div className="row">
                <div className="col-sm">
                  <form>
                    <input
                      value={partial}
                      onChange={(e) => onChange(e)}
                      name="partial"
                      className="form-control mb-4"
                      type="number"
                      placeholder="0.00"
                      step="0.05"
                      min="0"
                      max="1.0"
                    ></input>
                  </form>
                </div>
                
                <div className="col-sm"><button className="btn btn-info" onClick={addPartial}>Add</button></div>
              </div>
              <h5>Add full</h5>
              <div className="row">
                <div className="col-sm">
                  <button className="btn btn-danger" onClick={minusOne}>
                    <h4>-</h4>
                  </button>
                </div>
                <div className="col-sm">
                  <img
                    src={Bottle}
                    alt="Bottle Image"
                    height="140"
                    width="140"
                  />
                </div>
                <div className="col-sm">
                  <button className="btn btn-success" onClick={addOne}>
                    <h4>+</h4>
                  </button>
                </div>
              </div>
              <div className="row">
                <div className="col-sm"></div>
                <div className="col-sm">{quantity}</div>
                <div className="col-sm"></div>
              </div>
            </div>
            <div className="modal-footer">
              <button className="btn btn-success" data-dismiss="modal">
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default CountModal;
