import React, { Component } from "react";
import QrReader from "react-qr-reader";

const QrScan = () => {
  const state = {
    result: "No result",
  };

 const handleScan = (data) => {
    if (data) {
      this.setState({
        result: data,
      });
    }
  };
  const handleError = (err) => {
    console.error(err);
  };
  return (
    <div>
      <QrReader
        delay={300}
        onError={this.handleError}
        onScan={this.handleScan}
        style={{ width: "100%" }}
      />
      <p>{this.state.result}</p>
    </div>
  );
};

export default QrScan;
