import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import _ from "lodash";

const TopSellerChart = ({ data, labels }) => {
  return (
    <div>
      <div
        class="btn-group btn-group-sm float-right"
        role="group"
        aria-label="Basic example"
      >
        <button type="button" class="btn btn-light">
          This Week
        </button>
        <button type="button" class="btn btn-light">
          All Time
        </button>
      </div>
      <h6>Top Sellers</h6>
      <Bar
        data={{
          labels: labels,
          datasets: [
            {
              data: data,
            },
          ],
        }}
        options={{
          legend: {
            display: false,
          },
          title: {
            display: false,
            position: "left",
            text: "Top Selling Products",
          },
        }}
      />
    </div>
  );
};

export default TopSellerChart;
