import React, { PureComponent, useState } from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";

const PDFPredictedOrder = ({ levels }) => {
  // jspdf generator function
  const jsPdfGenerator = () => {
    //new document in jspdf

    var doc = new jsPDF("p", "pt");

    doc.autoTable({
      head: [["Product", "Size", "Category", "Quantity"]],
      body: levels.map((item) => {
        if (item.sum - item.avg_weekly_sales < 0) {
          return [
            item.product_id,
            item.product_name,
            item.product_size,
            item.product_category,
            Math.abs(Number(item.sum - item.avg_weekly_sales)) +
              item.avg_weekly_sales,
          ];
        }
      }),
    });

    doc.save("generated.pdf");
  };

  return (
    <button className="btn btn-info" onClick={jsPdfGenerator}>
      Generate PDF Report
    </button>
  );
};

export default PDFPredictedOrder;
