import React, { useState, useEffect } from "react";

import { toast } from "react-toastify";

//components
import ShowLists from "./ShowLists";

const StocktakeLists = ({ setAuth }) => {
  const [allLists, setLists] = useState([]);
  const [listsChange, setListsChange] = useState(false);

  const dateString = Date().toString();

  const createStocktake = () => {
    confirm(`Are you sure you want to start a new stocktake for ${dateString}`)
  }

  const getLists = async () => {
    try {
      const response = await fetch("http://localhost:5000/stocklists/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseData = await response.json();
      setLists(parseData);
    } catch (err) {
      console.error(err.message);
    }
  };

  

  const stocktake = true;

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logged out succsessfully!");
  };

  useEffect(() => {
    getLists();
    setListsChange(false);
  }, [listsChange]);

  return (
    <main role="main">
      <div className="jumbotron">
        <button
          className="btn btn-primary float-right"
          onClick={(e) => logout(e)}
        >
          Logout
        </button>
        <div className="container">
          <h1 className="display-3">Stock Areas</h1>
          <h4>Create a new stocktaking record for {dateString}</h4>
          <button onClick={createStocktake}>

          </button>
        </div>
      </div>
      <div>
        <ShowLists allLists={allLists} setListsChange={setListsChange} stocktake={stocktake} />
      </div>
    </main>
  );
};

export default StocktakeLists;
