import React, { PureComponent } from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";
import { useState, useEffect } from "react";

const PdfTest = ({ }) => {
  const [inventory, setInventory] = useState([]);

  const getInventory = async () => {
    const response = await fetch(
      `http://localhost:5000/inventory/records/`,
      {
        method: "GET",
        headers: { token: localStorage.token },
      }
    );
    const parseData = await response.json();
    setInventory(parseData);
  };

  // jspdf generator function
  const jsPdfGenerator = () => {
    //new document in jspdf

    var doc = new jsPDF("p", "pt");

    doc.autoTable({
      head: [["Name", "Email", "Country"]],
      body: [["David", "david@AudioParam.com", "Sweden"]],
    });

    doc.save("generated.pdf");
  };

  useEffect(() => {
    //getInventory();
  });

  return (
    <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
      <button className="btn btn-success" onClick={jsPdfGenerator}>
        Generate PDF Report{console.log(inventory)}
      </button>
    </main>
  );
};

export default PdfTest;
