import React, { Fragment, useState, useEffect } from "react";
import { Link, Redirect } from "react-router-dom";
import { FaBeer } from "react-icons/fa";
import { AiOutlineHome } from "react-icons/ai";
import { MdLocalBar } from "react-icons/md";
import { RiScales2Fill } from "react-icons/ri";
import { BsCardChecklist } from "react-icons/bs";
import TopSellerChart from "../components/TopSellerChart";

import { toast } from "react-toastify";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");
  const [stocktake, setStocktake] = useState(false);
  const [latestInventoryDate, setLatestInventoryDate] = useState("");
  const [latestInventoryId, setLatestInventoryId] = useState("");
  const [previousInventory, setPreviousInventory] = useState("");
  const [products, setProducts] = useState([]);


  async function getSalesData() {
    try {
      const response = await fetch("http://localhost:5000/products/get/distinct", {
        method: "GET", 
        headers: { token: localStorage.token },
      }
      );
      const parseRes = await response.json();
    } catch (error) {
      console.error(error.message);
    }
  }

  async function getSales() {
    try {
      
    } catch (error) {
      
    }
  }

  async function checkStocktake() {
    try {
      const response = await fetch(
        "http://localhost:5000/stocktake/activestocktake",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );

      const parseRes = await response.json();

      if (parseRes.length > 0) {
        setStocktake(parseRes);
        localStorage.setItem("stocktake", parseRes[0].stocktake_id);
        localStorage.setItem("stocktakedate", parseRes[0].stocktake_date);
      } else {
        console.log("no stocktake in progress");
      }
    } catch (error) {
      console.error(error.message);
    }
  }

  const stockId = Number(localStorage.getItem("stocktake"));

  const userType = localStorage.getItem("role");

  const Stocktake = () => {
    if (stockId !== 0) {
      console.log("stocktake in progress");
      setStocktake(true);
    }
  };

  async function findLastStocktake() {
    try {
      const response = await fetch("http://localhost:5000/inventory/latest", {
        method: "GET",
        headers: { token: localStorage.token },
      });
      const parseRes = await response.json();
      setLatestInventoryDate(parseRes[0].stocktake_date);
      setLatestInventoryId(parseRes[0].stocktake_id);
      setPreviousInventory(parseRes[1].stocktake_id);

      console.log(latestInventoryDate);
    } catch (error) {
      console.error(error.message);
    }
  }

  async function getName() {
    try {
      const response = await fetch("http://localhost:5000/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.user_name);
      console.log(userType);
    } catch (err) {
      console.log(err.message);
    }
  }

  const logout = (e) => {
    e.preventDefault();
    localStorage.clear();
    setAuth(false);
    toast.success("Logged out succsessfully!");
  };

  useEffect(() => {
    findLastStocktake();
    checkStocktake();
    Stocktake();
    getName();
    getSalesData();
  }, []);

  return userType == "User" ? (
    <Redirect to="/userdashboard" />
  ) : (
    <Fragment>
      <nav className="navbar navbar-dark sticky-top bg-dark flex-md-nowrap p-0">
        <a className="navbar-brand col-sm-3 col-md-2 mr-0" href="#">
          BevStock
        </a>
        <ul className="navbar-nav px-3">
          <li className="nav-item text-nowrap">
            <a className="nav-link" onClick={(e) => logout(e)} href="#">
              {name} : Sign out
            </a>
          </li>
        </ul>
      </nav>

      <div className="container-fluid">
        <div className="row">
          <nav className="col-md-2 d-none d-md-block bg-light sidebar">
            <div className="sidebar-sticky">
              <ul className="nav flex-column">
                <li className="nav-item">
                  <a className="nav-link active" href="#">
                    <AiOutlineHome /> Dashboard{" "}
                    <span className="sr-only">(current)</span>
                  </a>
                </li>
                <li className="nav-item">
                  <Link className="nav-item nav-link" to="/stocktaking">
                    <MdLocalBar /> View/Edit Stock Areas
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/stocktakelists">
                    <RiScales2Fill />
                    {stocktake
                      ? "  Continue Stocktake"
                      : "  Perform a Stocktake"}
                  </Link>
                </li>
                <li className="nav-item">
                  <Link
                    className="nav-link"
                    to={`/inventory/${latestInventoryId}`}
                  >
                    <BsCardChecklist /> Inventory
                  </Link>
                </li>
                <li className="nav-item"></li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    <span data-feather="layers"></span>
                    Integrations
                  </a>
                </li>
              </ul>

              <h6 className="sidebar-heading d-flex justify-content-between align-items-center px-3 mt-4 mb-1 text-muted">
                <span>Saved reports</span>
                <a className="d-flex align-items-center text-muted" href="#">
                  <span data-feather="plus-circle"></span>
                </a>
              </h6>
              <ul className="nav flex-column mb-2">
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    <span data-feather="file-text"></span>
                    Current month
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    <span data-feather="file-text"></span>
                    Last quarter
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    <span data-feather="file-text"></span>
                    Social engagement
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">
                    <span data-feather="file-text"></span>
                    Year-end sale
                  </a>
                </li>
              </ul>
            </div>
          </nav>

          <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pb-2 mb-3 border-bottom">
              <h1 className="h2">Dashboard</h1>
              <div className="btn-toolbar mb-2 mb-md-0">
                <div className="btn-group mr-2">
                  <button className="btn btn-sm btn-outline-secondary">
                    Share
                  </button>
                  <button className="btn btn-sm btn-outline-secondary">
                    Export
                  </button>
                </div>
                <button className="btn btn-sm btn-outline-secondary dropdown-toggle">
                  <span data-feather="calendar"></span>
                  This week
                </button>
              </div>
            </div>

            <div className="row">
            <TopSellerChart products={products} currentStocktake={latestInventoryId} prevStocktake={previousInventory} />
            </div>

            

            <h2>Section title</h2>
            <div className="table-responsive">
              <table className="table table-striped table-sm">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Header</th>
                    <th>Header</th>
                    <th>Header</th>
                    <th>Header</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>1,001</td>
                    <td>Lorem</td>
                    <td>ipsum</td>
                    <td>dolor</td>
                    <td>sit</td>
                  </tr>
                  <tr>
                    <td>1,002</td>
                    <td>amet</td>
                    <td>consectetur</td>
                    <td>adipiscing</td>
                    <td>elit</td>
                  </tr>
                  <tr>
                    <td>1,003</td>
                    <td>Integer</td>
                    <td>nec</td>
                    <td>odio</td>
                    <td>Praesent</td>
                  </tr>
                  <tr>
                    <td>1,003</td>
                    <td>libero</td>
                    <td>Sed</td>
                    <td>cursus</td>
                    <td>ante</td>
                  </tr>
                  <tr>
                    <td>1,004</td>
                    <td>dapibus</td>
                    <td>diam</td>
                    <td>Sed</td>
                    <td>nisi</td>
                  </tr>
                  <tr>
                    <td>1,005</td>
                    <td>Nulla</td>
                    <td>quis</td>
                    <td>sem</td>
                    <td>at</td>
                  </tr>
                  <tr>
                    <td>1,006</td>
                    <td>nibh</td>
                    <td>elementum</td>
                    <td>imperdiet</td>
                    <td>Duis</td>
                  </tr>
                  <tr>
                    <td>1,007</td>
                    <td>sagittis</td>
                    <td>ipsum</td>
                    <td>Praesent</td>
                    <td>mauris</td>
                  </tr>
                  <tr>
                    <td>1,008</td>
                    <td>Fusce</td>
                    <td>nec</td>
                    <td>tellus</td>
                    <td>sed</td>
                  </tr>
                  <tr>
                    <td>1,009</td>
                    <td>augue</td>
                    <td>semper</td>
                    <td>porta</td>
                    <td>Mauris</td>
                  </tr>
                  <tr>
                    <td>1,010</td>
                    <td>massa</td>
                    <td>Vestibulum</td>
                    <td>lacinia</td>
                    <td>arcu</td>
                  </tr>
                  <tr>
                    <td>1,011</td>
                    <td>eget</td>
