import React, { Fragment, useState, useEffect } from "react";

const ShowTeam = ({ teamMembers, setTeamChange }) => {
  const [team, setTeamMembers] = useState([]); // assigned to an empty array

  async function deleteTeamMember(id) {
    try {
      await fetch(`http://localhost:5000/teammanagement/delete/${id}`, {
        method: "DELETE",
        headers: { token: localStorage.token }
      });

    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    setTeamMembers(teamMembers);
  }, [teamMembers]);

  console.log(teamMembers);

  return (
    <Fragment>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Name</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {}
          {teamMembers.map((member) => (
            <tr key={member.user_id}>
              <td>{member.user_name}</td>
              <td><button className="btn btn-danger" onClick={() => deleteTeamMember(member.user_id) } >Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ShowTeam;
