import React from "react";
import { useEffect } from "react";
import { useState, useRef } from "react";
import Product from "./Product";
import "../../../src/App.css";

const DndTest = () => {
  const [products, setProducts] = useState([]);
  const [dragging, setDragging] = useState(false);
  const [originalIndex, setOriginalIndex] = useState('')
  const dragItem = useRef();
  const dragNode = useRef();

  //get products on list
  const getProducts = async () => {
    try {
      const response = await fetch(`http://localhost:5000/stocklists/list/44`, {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setProducts(parseRes);
      console.log(parseRes);
    } catch (err) {
      console.error(err.message);
    }
  };

  const handleDragStart = (e, params) => {
    console.log("drag start", params);
    dragItem.current = params;
    dragNode.current = e.target;
    setOriginalIndex(params);
    dragNode.current.addEventListener("dragend", handleDragEnd);
    setTimeout(() => {
      setDragging(true);
    }, 0);
  };

  const handleDragEnter = (e, params) => {
    console.log('Entering drag', params)
    console.log(originalIndex);
    if (e.target !== dragNode.current.productI) {
      console.log(params);
      console.log("Target is not the same")
    }
  }

  const handleDragEnd = () => {
    console.log("Ending Drag");
    setDragging(false);
    dragNode.current.removeEventListener("dragend", handleDragEnd);
    dragItem.current = null;
    dragNode.current = null;
  };

  const getStyles = (params) => {
    const currentItem = dragItem.current;
    if (currentItem.productI === params) {
      return "current dnd-item";
    }
    return "dnd-item";
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <div>
      <h1>TEST</h1>
      <header>
        <div className="drag-n-drop">
          <div className="dnd-group">
            {products.map((product, productI) => (
              <div
                draggable
                onDragStart={(e) => {
                  handleDragStart(e, { productI });
                }}
                onDragEnter={dragging ?(e) => {handleDragEnter(e, {productI})} : null }
                key={product.product_stocklist_id}
                className={dragging ? getStyles(productI) : "dnd-item"}
              >
                <Product
                  dragging={dragging}
                  key={product.product_stocklist_id}
                  name={product.product_name}
                  id={product.product_stocklist_id}
                />
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
};

export default DndTest;
