import React, { Fragment } from "react";
import { useState } from "react";

const AddNewProduct = () => {
  const [inputs, setInputs] = useState({
    name: "",
    size: "",
    par: "",
  });
  const onChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };
  return (
    <Fragment>
      <button
        type="button"
        className="btn-sm btn-primary form-control"
        data-toggle="modal"
        data-target="#product-modal"
      >
        Add New Product
      </button>

      <div id="product-modal" className="modal fade" role="dialog">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Add a new Product</h4>
              <button type="button" className="close" data-dismiss="modal">
                &times;
              </button>
            </div>
            <div className="modal-body">
              <form onSubmit={onSubmitForm}>
                <input
                  type="text"
                  name="name"
                  placeholder="Product Name"
                  className="form-control my-3"
                  value={name}
                  onChange={(e) => onChange(e)}
                />
                <input
                  type="number"
                  name="size"
                  placeholder="Product Size"
                  className="form-control my-3"
                  value={size}
                  onChange={(e) => onChange(e)}
                />
                <input
                  type="number"
                  name="par"
                  placeholder="Par Level"
                  className="form-control my-3"
                  value={par}
                  onChange={(e) => onChange(e)}
                />
                <select className="form-control my-3" id="category">
                  <option value="Rum">Vodka</option>
                  <option value="Vodka">Rum</option>
                </select>
                <button className="btn btn-success btn-block">Submit</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AddNewProduct;
