import React from "react";

const ProductAlerts = ({ levels }) => {
  return (
    <div>
    <h6 className="mb-3">Low Product Alerts</h6>
      <table className="table table-striped table-sm">
        <thead>
          <tr>
            <th>Product</th>
            <th>Recomended</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {levels.map((item) => {
            if (item.par_level > Number(item.sum))
              return (
                <tr key={item.product_id}>
                  <td>{item.product_name}</td>
                  <td>{item.par_level}</td>
                  {Number(item.sum) === 0 ?<td><span className="badge badge-pill badge-danger">Out Of Stock</span></td>: <td><span className="badge badge-pill badge-warning">Running Low: {item.sum}</span></td>}
                </tr>
              );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ProductAlerts;
