import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import _ from "lodash";

const Test2 = () => {
  const [products, setProducts] = useState([]);
  const prevStocktake = localStorage.getItem("secondlaststocktake");
  const currentStocktake = localStorage.getItem("laststocktake");
  const [sales, setSales] = useState([]);
  const [data, setData] = useState();
  const [labels, setLabels] = useState([]);
  const [productsChange, setProductsChange] = useState([]);

  async function getProducts() {
    try {
      const response = await fetch(
        "http://localhost:5000/products/get/distinct",
        {
          method: "GET",
          headers: { token: localStorage.token },
        }
      );
      const parseRes = await response.json();
      setProducts(parseRes);
      setProductsChange(true);
    } catch (error) {
      console.error(error.message);
    }
  }

  const promiseAll = async (sales) => {
    await Promise.all(sales).then(
      sales.forEach((sale) => {
        console.log(sale);
      })
    );
  };

  const asyncForEach = async (array, callback) => {
    for (let index = 0; index < array.length; index++) {
      await callback(array[index], index, array);
    }
  };

  const getSalesNumbers = async (products) => {
    var promises = products.map((product) => {
      return getSalesData(product.product_id).then((results) => {
        return results;
      });
    });
    Promise.all(promises).then((results) => {
      setSales(
        results
          .sort((a, b) => {
            if (Number(a.rows.sales) < Number(b.rows.sales)) {
              return 1;
            } else if (Number(a.rows.sales) > Number(b.rows.sales)) {
              return -1;
            } else {
              return 0;
            }
          })
          .slice(0, 5)
      );
    });
  };

  const getSalesData = async (product) => {
    try {
      const body = { prevStocktake, currentStocktake, product };
      const response = await fetch(`http://localhost:5000/inventory/sales`, {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(body),
      });
      return await response.json();
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    getProducts();
    if (!_.isEmpty(products)) {
      Promise.all(products)
        .then(getSalesNumbers(products))
        .then(console.log(sales))
        .then(console.log(labels));
      Promise.all(sales).then(
        sales.forEach((sale) => {
          console.log(sale);
        })
      );
    }
  }, [productsChange]);

  return (
    <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">
      TEST2
      {console.log(labels)}
    </main>
  );
};

export default Test2;
