import React, { Fragment, useState, useEffect } from "react";

import EditQuantity from "./EditQuantity";

const Stocklists = (props) => {
  
  const [lists, setLists] = useState([]);

  
  const getLists = async () => {
    try {
      const response = await fetch("http://localhost:5000/stocklists");
      const jsonData = await response.json();

      setLists(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getLists();
  }, []);



  return (
    <Fragment>
    <main role="main">
        <div className="jumbotron">
          <div className="container">
            <h1 className="display-3">Stock Areas</h1>
            <p>
              Welcome to your BevStock dashboard.
            </p>
          </div>
        </div>
      <h1 className="text-center mt-5">Product List</h1>
      <table className="table mt-5 text-center">
        <thead>
          <tr>
            <th>Stock Area</th>
            <th>Quantity</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/*<tr>
                        <td>John</td>
                        <td>Doe</td>
                        <td>john@example.com</td>
                    </tr>*/}
          {lists.map((stocklist) => (
            <tr key={stocklist.stocklist_id}>
              <td>{stocklist.stocklist_name}</td>
              <td>
                <EditQuantity product={product} />
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteProduct(products.product_id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </main>
    </Fragment>
  );
};

export default Stocklists;
