import React, { useState, useEffect } from "react";

import { toast } from "react-toastify";

//components
import ShowLists from "./ShowLists";

const StocktakeLists = ({ setAuth }) => {
  const [stocktake, setStocktake] = useState(false);
  const [allLists, setLists] = useState([]);
  const [listsChange, setListsChange] = useState(false);
  const [generated, setGenerated] = useState(false);

  const dateString = Date().toString();

  const stocktake_id = localStorage.getItem("stocktake");

  const checkStocktake = () => {
    if (stocktake_id !== null) {
      console.log("stocktake in progress");
      setStocktake(true);
    }
  };

  const createStocktake = () => {
    window.confirm("Sure?") ? setStocktake(true) : setStocktake(false);
    console.log(stocktake);
  };

  const getProducts = async (id) => {
    const products =([])
    try {
      const productsResponse = await fetch("http://localhost:5000/stocktake/", {
        method: "GET",
        headers: { token: localStorage.token },
      });
      const productsResult = await productsResponse.json();
      let products = productsResult.rows;
    } catch (error) {
      console.error(error.message);
    }
    return products;
  };

  const generateRecords = async () => {
    await getProducts();
  };

  const newStocktake = async () => {
    if (stocktake && stocktake_id === null) {
      console.log("hello");
      try {
        const response = await fetch("http://localhost:5000/stocktake/new", {
          method: "POST",
          headers: { token: localStorage.token },
        });
        const result = await response.json();
        localStorage.setItem("stocktake", result.rows[0].stocktake_id);
        localStorage.setItem("stocktakedate", result.rows[0].stocktake_date);
      } catch (err) {
        console.log(err.message);
      }
    }
  };

  const getLists = async () => {
    try {
      const response = await fetch("http://localhost:5000/stocklists/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseData = await response.json();
      setLists(parseData);
    } catch (err) {
      console.error(err.message);
    }
  };

  const endStocktake = (e) => {
    e.preventDefault();
    localStorage.removeItem("stocktake");
    localStorage.removeItem("stocktakedate");
    setStocktake(false);
    toast.success("Stocktake ended");
  };

  const logout = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
    toast.success("Logged out succsessfully!");
  };

  useEffect(() => {
    checkStocktake();
    getLists();
    newStocktake();
    setListsChange(false);

    generateRecords();
  }, [listsChange, stocktake]);

  return !stocktake_id ? (
    <main role="main">
      <div className="jumbotron">
        <button
          className="btn btn-primary float-right"
          onClick={(e) => logout(e)}
        >
          Logout
        </button>
        <div className="container">
          <h1 className="display-3">Stocktake</h1>
          <h4>Create a new stocktaking record for {dateString}?</h4>
          <button className="btn btn-info" onClick={createStocktake}>
            Start Stocktake
          </button>
        </div>
      </div>
    </main>
  ) : (
    <main role="main">
      <div className="jumbotron">
        <button
          className="btn btn-danger float-right"
          onClick={(e) => endStocktake(e)}
        >
          End Stocktake
        </button>
        <div className="container">
          <h1 className="display-3">Stocktake</h1>
          <h4>
            Stocktake in progress : {localStorage.getItem("stocktakedate")}
          </h4>
        </div>
      </div>
      <div>
        <ShowLists
          allLists={allLists}
          setListsChange={setListsChange}
          stocktake={stocktake}
        />
      </div>
    </main>
  );
};

export default StocktakeLists;
