import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import Product from "./Product";
import "../../../src/App.css";

const DndTest = () => {
  const [products, setProducts] = useState([]);

  //get products on list
  const getProducts = async () => {
    try {
      const response = await fetch(`http://localhost:5000/stocklists/list/44`, {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setProducts(parseRes);
      console.log(parseRes);
    } catch (err) {
      console.error(err.message);
    }
  };

  const handleDragStart = (e, params) => {
    console.log('drag start', params);
  }

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <div>
      <h1>TEST</h1>
      <header>
        <div className="drag-n-drop">
          <div className="dnd-group">
            {products.map((product, productI) => (
              <div draggable onDragStart={(e) => {handleDragStart(e, {productI})}} key={product.product_stocklist_id} className="dnd-item">
                <Product
                  key={product.product_stocklist_id}
                  name={product.product_name}
                  id={product.product_stocklist_id}
                />
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
};

export default DndTest;
